import React from 'react'
import { connect } from 'react-redux'

function SongDetail({song}) {
    if(song)
    return (
        <div>
            {song.title}
        </div>
    )
}

const mapStateToProps = (state) => {
    return { song: state.selectedSong}
}

export default connect(mapStateToProps)(SongDetail)
